import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import IconButton from "@material-ui/core/IconButton";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import Rating from "@material-ui/lab/Rating";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import { connect } from "react-redux";
import updatePosts from "../../store/actions/updatePosts";
import fetchUsers from "../../store/actions/fetchUsers";
import fetchArticles from "../../store/actions/fetchArticles";
import Popup from "../common/Popup";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

function MediaCard(props) {
  const classes = useStyles();
  // Similar to componentDidMount and componentDidUpdate:
  useEffect(() => {
    // Update the document title using the browser API
    props.fetchArticles();
  }, []);

  const { articles, history } = props;
  // const cardClickHandler = (article) => {
  //   history.push("/editproduct/" + article.productId, { articles: article });
  // };
  console.log(props.articles, "articles working cool");
  //console.log(users, "data in props");
  return (
    <div className="parent-card">
      {articles.map((article) => (
        <div className="card-box">
          <React.Fragment>
            <Card className={classes.root}>
              <CardHeader
                avatar={
                  <Avatar aria-label="recipe" className={classes.avatar}>
                    W
                  </Avatar>
                }
                action={
                  <IconButton aria-label="settings">
                    <Link
                      to={{
                        pathname: `/editproduct/${article.productId}`,
                        articleProps: {
                          productName: article.productName,
                          shortDescription: article.shortDescription,
                          longDescription: article.longDescription,
                          price: article.price,
                          reviewRating: article.reviewRating,
                        },
                      }}
                    >
                      <MoreVertIcon />
                    </Link>
                  </IconButton>
                }
                title={article.productName}
                //subheader="September 14, 2016"
                style={{ textAlign: "left" }}
              />
              <CardMedia
                className={classes.media}
                image={article.productImage}
                title={article.productName}
              />
              <CardContent>
                <Typography
                  variant="body2"
                  color="textSecondary"
                  component="p"
                  style={{ textAlign: "left" }}
                >
                  {article.shortDescription}
                </Typography>
              </CardContent>
              <CardActions>
                <Rating name="simple-controlled" value={article.reviewRating} />
                <Typography
                  display="block"
                  variant="caption"
                  color="textPrimary"
                >
                  {article.productPrice}
                </Typography>
                <Typography
                  display="block"
                  variant="caption"
                  color="textPrimary"
                  style={{ marginLeft: "40rem", fontSize: "18px" }}
                >
                  {article.productInStock}
                </Typography>
              </CardActions>
            </Card>
          </React.Fragment>
        </div>
      ))}
    </div>
  );
}

//Make State accessible to movies and users in App.
const MapStateToProps = (state) => {
  return {
    posts: state.posts,
    users: state.users,
    articles: state.articles,
  };
};

// Setup Dispatch for our button events.
const MapDispatchToProps = (dispatch) => {
  return {
    updatePosts: () => dispatch(updatePosts),
    fetchUsers: () => dispatch(fetchUsers),
    fetchArticles: () => dispatch(fetchArticles),
  };
};

export default connect(MapStateToProps, MapDispatchToProps)(MediaCard);
