export const UPDATE_POSTS = "UPDATE_POSTS";

//Creating an object for our action to update the movie list.
//Setting the payload to the movie of our choice as a string.

// const updatePosts = {
//   type: UPDATE_POSTS,
//   payload: "THE BREAKFAST CLUB",
// };

const updatePosts = (dispatch) => {
  fetch(
    "https://xandar.pinnium.in/api/dive-in/articles/?userId=5eca75d8e79555001d4d36f7"
  )
    .then((res) => res.json())
    .then((res) => dispatch({ type: UPDATE_POSTS, payload: res.data }));
};

export default updatePosts;
