import React from "react";
import TextInput from "./TextInput";
//import SelectInput from "../common/SelectInput";
import { connect } from "react-redux";
import Button from "@material-ui/core/Button";
import { useHistory } from "react-router-dom";
import Rating from "@material-ui/lab/Rating";
import { fetchProduct } from "../redux/fetchProducts";

function ProductEditForm(props) {
  const history = useHistory();
  console.log("user props: ", props);
  const { article } = props.location.state;
  console.log("user: ", article);
  onChange = (event, value) => {
    console.log(value, "onchange event handler");
  };
  return (
    <div className="row" style={{ paddingBottom: "4rem" }}>
      <h1 style={{ paddingTop: "2rem", paddingBottom: "1rem" }}>
        Edit Product Page
      </h1>
      <TextInput
        name="title"
        label="Product Name"
        value={article.productName}
        //onChange={onChange}
        //error={errors.title}
      />
      <TextInput
        name="title"
        label="Product Image URL"
        value={`https://mobile-tha-server-8ba57.firebaseapp.com/${article.productImage}`}
        //onChange={onChange}
        //error={errors.title}
      />
      <TextInput
        name="title"
        label="Product Short Description"
        value={article.shortDescription}
        //onChange={onChange}
        //error={errors.title}
      />
      <TextInput
        name="title"
        label="Product Long Description"
        value={article.longDescription}
        //onChange={onChange}
        //error={errors.title}
      />
      <TextInput
        name="title"
        label="Product Price"
        value={article.price}
        //onChange={onChange}
        //error={errors.title}
      />
      <TextInput
        name="title"
        label="Product Review Rating"
        value={article.reviewRating}
        //onChange={onChange}
        //error={errors.title}
      />

      <Button
        variant="contained"
        color="secondary"
        onClick={() => this.props.history.push("/")}
      >
        Back to home page
      </Button>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    article: state.article,
  };
};

export default connect(mapStateToProps)(ProductEditForm);
