import { UPDATE_POSTS } from "../actions/updatePosts";

// Recieve type and payload from action and return payload.
// Movies are being updated by the action of the string in our payload.

const postlistReducer = (state = {}, { type, payload }) => {
  switch (type) {
    case UPDATE_POSTS:
      return { payload };
    default:
      return state;
  }
};

export default postlistReducer;
